const PLANTATION_ID = msg.payload.plantation_id
const SENSOR_ID = msg.payload.sensor_id

msg.topic = `plantation/${PLANTATION_ID}/sensor/${SENSOR_ID}/event/reading`

const now = new Date()
now.setUTCSeconds(0)
now.setUTCMilliseconds(0)

msg.payload = {
    measure_timespan: "AVG_1_MIN",
    measurement: Math.random() * msg.payload.multiplier,
    reading_timestamp: now.toISOString()
}

return msg;
